import axios from "axios";

const Universities = {
    state: {
        university: [
            /*
            "subjects": [{id: 1, name: "математика"}, "физика", "русский язык", "казахский язык"],
            {
                "name": "Южно-Казахстанский государственный университет им. Жангир Хана (ЮКГУЖХ).",
                "status": "государственный",
                "subjects": ["математика", "физика", "русский язык", "казахский язык"],
                "image": require("@/images/Universities/logos/zhangir.png"),
                "description": "ЮКГУЖХ был основан в 2014 году и предлагает обучение по специальностям в области экономики, бизнеса, права, гуманитарных и социальных наук. Университет активно сотрудничает с международными университетами и организациями.",
                "benefits": "широкий выбор специальностей, сотрудничество с международными университетами и организациями.",
                "tags": ["Education", "Natural Sciences", "Humanities"],
                "location": "Шымкент",
                "passScores": 55,
                "budget": 800,
                "offBudget": 150,
                "hasHotel": true,
                "hasMilitaryDepartment": false
            },
            */
        ],
    },
    getters: {
        getUniversity(state) {
            return state.university
        }
    },
    mutations: {
        setAllUniversities(state, value) {
            state.university = value
        }
    },
    actions: {
        async setAllUniversities({commit}) {
            const universities = await axios.get("universities");
            commit("setAllUniversities", universities.data)
        }
    }
}

export default Universities
