import axios from "axios";

const users = {
    state: {
        users: [
            /*{
                id: 2,
                username: 'guest',
                email: 'guest@gmail.com',
                password: 'guestPass',
                role: 'user',
                location: 'Нур-Султан',
                history: []
            },*/
        ],
    },
    getters: {
        getUsers(state) {
            return state.users
        }
    },
    mutations: {
        setUsers(state, payload) {
            state.users[payload.id].username = payload.username;
            state.users[payload.id].email = payload.email;
            state.users[payload.id].password = payload.password;
            state.users[payload.id].role = payload.role;
            state.users[payload.id].location = payload.location;
        },
        removeUser(state, index) {
            state.users.splice(index, 1);
        },
        setAllUsers(state, value) {
            state.users = value
        }
    },
    actions: {
        async setAllUsers({commit}) {
            const users = await axios.get('user/all');
            commit('setAllUsers', users.data)
        }
    }
}

export default users
