const users = {
    state: {
        users: [
            {
                id: 1,
                username: 'user',
                email: 'mail@gmail.com',
                password: 'qwerty',
                role: 'admin',
                location: 'Алматы',
                history: []
            },
            {
                id: 2,
                username: 'guest',
                email: 'guest@gmail.com',
                password: 'guestPass',
                role: 'user',
                location: 'Нур-Султан',
                history: []
            },
            {
                id: 3,
                username: 'third',
                email: 'third@gmail.com',
                password: 'third',
                role: 'user',
                location: 'Туркестан',
                history: []
            }
        ],
    },
    getters: {
        getUsers(state) {
            return state.users
        }
    },
    mutations: {
        setUsers(state, payload) {
            state.users[payload.id].username = payload.username;
            state.users[payload.id].email = payload.email;
            state.users[payload.id].password = payload.password;
            state.users[payload.id].role = payload.role;
            state.users[payload.id].location = payload.location;
        },
        removeUser(state, index) {
            state.users.splice(index, 1);
        }
    }
}

export default users
