const users = {
    state: {
        users: [
            {
                name: 'user',
                mail: 'mail@gmail.com',
                password: 'qwerty',
                role: 'admin',
                location: 'Алматы',
                history: []
            },
            {
                name: 'guest',
                mail: 'guest@gmail.com',
                password: 'guestPass',
                role: 'user',
                location: 'Нур-Султан',
                history: []
            },
            {
                name: 'third',
                mail: 'third@gmail.com',
                password: 'third',
                role: 'user',
                location: 'Туркестан',
                history: []
            }
        ],
    },
    getters: {
        getUsers(state) {
            return state.users
        }
    },
    mutations: {
        setUsers(state, payload) {
            state.users[payload.id].name = payload.name;
            state.users[payload.id].mail = payload.mail;
            state.users[payload.id].password = payload.password;
            state.users[payload.id].status = payload.role;
            state.users[payload.id].location = payload.location;
        },
        removeUser(state, index) {
            state.users.splice(index, 1);
        }
    }
}

export default users
