import { createRouter, createWebHistory } from 'vue-router'
import Tests from "@/views/Tests.vue";
import ProfOrientation from "@/views/ProfOrientation.vue";
import Test from "@/views/Test.vue";
import Result from "@/views/Result.vue";
import PageNotFound from "@/views/PageNotFound.vue";
import Reset from "@/views/Reset.vue";
import FAQ from "@/views/FAQ.vue";
import Professions from "@/views/Professions.vue";
import exactProfessions from "@/views/ExactProfessions.vue";
import Profession from "@/views/Profession.vue";
import Account from "@/views/Account.vue";
import store from "@/store";

const routes = [
  {
    path: '/',
    redirect: '/tests'
  },
  {
    path: '/tests',
    name: 'tests',
    component: Tests
  },
  {
    path: '/prof',
    name: 'prof',
    component: ProfOrientation
  },
  {
    path: '/test',
    name: 'test',
    component: Test
  },
  {
    path: '/result',
    name: 'result',
    component: Result
  },
  {
    path: '/reset',
    name: 'reset',
    component: Reset
  },
  {
    path: '/professions',
    name: 'Professions',
    component: Professions
  },
  {
    path: '/professions/:name',
    name: 'Exact_Professions',
    component: exactProfessions
  },
  {
    path: '/professions/:name/:prof',
    name: 'Profession',
    component: Profession
  },
  {
    path: '/faq',
    name: 'faq',
    component: FAQ
  },
  {
    path: '/account',
    name: 'Account',
    component: Account,
    meta: {
      requiresAuth: true
    }
  },
  {
    path: '/history',
    name: 'History',
    component: History
  },
  {
    path: "/:pathMatch(.*)*",
    component: PageNotFound
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
  linkActiveClass: "active",
  linkExactActiveClass: "exact-active",
})

// Validation for auth
// If the user is not logged in and tries to go to special pages for users, then he redirects to the tests page
router.beforeEach((to, from, next) => {
  if (to.matched.some(record => record.meta.requiresAuth)) {
    // this route requires auth, check if logged in
    if (!store.getters.getAuth) {
      next({ name: 'tests' })
    } else {
      next() // go to wherever I'm going
    }
  } else {
    next() // does not require auth, make sure to always call next()!
  }
})

export default router
