import { createStore } from 'vuex'

export default createStore({
  state: {
    isAuth: false,
    userId: null, // TODO: Сделать так, чтобы сюда писался юзер айди для того, чтобы найти авторизованного пользователя из массива. Хочу использовать при написании комментария, от чьего имени писать :) Время 2 часа ночи 10 Марта, усталлл....
    openedAuth: false,
    users: [
      {
        userId: 1,
        name: 'user',
        mail: 'mail@gmail.com',
        password: 'qwerty'
      },
      {
        userId: 2,
        name: 'guest',
        mail: 'guest@gmail.com',
        password: 'guestPass'
      }
    ]
  },
  getters: {
    getAuth(state) {
      return state.isAuth
    },
    getOpenedAuth(state) {
      return state.openedAuth
    },
    getUsers(state) {
      return state.users
    }
  },
  mutations: {
    toggleOpenedAuth(state) {
      state.openedAuth = !state.openedAuth;
    },
    setAuth(state) {
      state.isAuth = !state.isAuth
    },
    setUserId(state, value) {
      state.userId = value
    }
  },
  actions: {
  },
  modules: {
  }
})
